import React, { useEffect, useState } from 'react';
import {
  XYPlot,
  XAxis,
  YAxis,
  HorizontalGridLines,
  VerticalGridLines,
  LineMarkSeries,
  LineSeries,
  MarkSeries,
  VerticalRectSeries,
  Hint,
} from 'react-vis';

const { default: axios } = require('axios');

const ONE_DAY = 86400000;

// A card used to contain all data from api
export default function Graphs(){

    const [events, setEvents] = useState([]);

    const fetchEvents = async () => {
        await axios.get('http://localhost:9000/albums').then(all => setEvents(all.data));
    }

    useEffect(() => {
        fetchEvents();
        //console.log( {events} );
    }, []);

    var datas = [];
    for (let i = 0; i < events.length; i++) {
        datas.push({x: i, y: events[i].Album.ImageCount});
    }
    console.log("coucou");


    /**
     * Same as precedent datas just added the real time to put them in the chart and order them
     * Elements are stored like that 
     * "LastUpdated": "2021-09-24 04:59:39"
     */
    var datasTime = [];
    for (let i = 0; i < events.length; i++) {
        datasTime.push(
            {
                x0: new Date(events[i].Album.LastUpdated).getTime(), 
                x:  new Date(events[i].Album.LastUpdated).getTime() + ONE_DAY, 
                y:  events[i].Album.ImageCount
            }
        );
    }

    var value=null;

    return (
        <div className='chart'>
            <XYPlot width={500} height={500} margin={70} >
                <HorizontalGridLines style={{stroke: '#B7E9ED'}} />
                <VerticalGridLines style={{stroke: '#B7E9ED'}} />
                <XAxis class="xaxis"
                    title="Numéro de l'évènement"
                    style={{
                        fill: 'var(--text)',
                        line: {stroke: '#ADDDE1'},
                        ticks: {stroke: '#ADDDE1'},
                        text: {stroke: 'none', fill: 'var(--text)', fontWeight: 600}
                    }}
                />
                <YAxis class="yaxis" title="Nombre de photos" style={{fill: 'var(--text)', text: {stroke: 'none', fill: 'var(--text)', fontWeight: 600}}}/>
                <LineSeries
                    className="first-series"
                    data={datas}
                    stroke='var(--text)'
                    style={{
                        strokeWidth: 2.5,
                        fill: 'none'
                    }}
                />
                <MarkSeries
                    className="second-series"
                    data={datas}
                    stroke='var(--bg-secondary)'
                    color='var(--text)'
                    style={{
                        strokeWidth: 1,
                        fill: 'var(--bg-secondary)'
                    }}
                    onValueMouseOver={(datapoint, event) => {value=datapoint; console.log(value);}}
                    onValueMouseOut={ () => {value=null; console.log(value);}}
                    onValueClick={(datapoint, event) => {
                        console.log(datapoint + " " + events[datapoint.x].Album.Title);
                        value=datapoint;
                        console.log(value);
                        <Hint value={value} />
                    }}
                />
                <Hint value={{x:7, y:72}}/>
                {value ? <Hint value={value} /> : null}
            </XYPlot>
            <div className='list'>
                <List events={events}/>
            </div>
            <XYPlot
                margin={70}
                xType="time"
                width={1000}
                height={600}>
                <VerticalGridLines />
                <HorizontalGridLines />
                <XAxis tickLabelAngle={-45}/>
                <YAxis />
                <VerticalRectSeries data={datasTime} style={{stroke: 'var(--text)'}} />
            </XYPlot>
        </div>
    )
}

function List(props){

    var list = [];

    for (let i = 0; i < props.events.length; i++) {
        list.push({
            "id":i,
            "date": props.events[i].Album.LastUpdated,
            "nom": props.events[i].Album.Title,
            "nb": props.events[i].Album.ImageCount
        });
    }

    return(
        <>
            {list.map(event => (
                <div className='event'>
                    <h3 className='lieu'>N°{event.id}</h3>
                    <h5 className='date'>Date : {event.date}</h5>
                    <h5 className='nom'>Nom : {event.nom}</h5>
                    <h5 className='photo'>Nb photos : {event.nb}</h5>
                </div>
            ))}
        </>
    );
}

function handleClick(props,i){
    console.log("oui");

    return (
        <>
            <Hint value={i} style={{
                fontSize: 14,
                value: {
                    color: 'red'
                }}}>
            test
            </Hint>
        </>
    );
}
