import React, { useEffect, useState } from 'react';
import {
  XYPlot,
  XAxis,
  YAxis,
  HorizontalGridLines,
  VerticalGridLines,
  LineSeries
} from 'react-vis';
const { default: axios } = require('axios');

// A card used to contain all data from api
export default function Graphs(){

    const [events, setEvents] = useState([]);

    const fetchEvents = async () => {
        await axios.get('http://localhost:9000/albums').then(all => setEvents(all.data));
    }

    useEffect(() => {
        fetchEvents();
        //console.log( {events} );
    }, []);

    var datas = [];
    for (let i = 0; i < events.length; i++) {
        datas.push({x: i, y: events[i].Album.ImageCount});
    }
    console.log("coucou");

    return (
        <div className='chart'>
            <XYPlot width={'20%'} height={'50%'}>
                <HorizontalGridLines style={{stroke: '#B7E9ED'}} />
                <VerticalGridLines style={{stroke: '#B7E9ED'}} />
                <XAxis
                    title="Numéro de l'évènement"
                    style={{
                        line: {stroke: '#ADDDE1'},
                        ticks: {stroke: '#ADDDE1'},
                        text: {stroke: 'none', fill: '#6b6b76', fontWeight: 600}
                    }}
                />
                <YAxis title="Y Axis" />
                <LineSeries
                    className="first-series"
                    data={datas}
                    style={{
                        strokeWidth: 4
                    }}
                />
            </XYPlot>
        </div>
    )

}
