import React from 'react';
import './admin.css';


// les trois profils et leur contenu

let profils = [
  {
    nom: 'Davodet',
    prenom: 'Aurélien',
    date: '24/04/98',
    photo:'aurel.png',
    publi: "Le React c'est trop coool",
    nbLike: 0 //nombre de like (0 par défaut)
  },
  {
    nom: 'Marze',
    prenom: 'Oscar',
    date: '04/04/99',
    photo:'oscar.png',
    publi: "J'aime la vie",
    nbLike: 0
  },
  {
    nom: 'Maréchal',
    prenom: 'Julien',
    date: '03/08/99',
    photo:'julien.png',
    publi: "Je préfère les pâtes au riz",
    nbLike: 0
  }
];

//numéro du profil par défaut
let numProf =0;


//class qui permet d'afficher les informations de chaque profil

class Profil extends React.Component{


  render() {

    if(this.props.prof==0){
      return (

        <div>
          <center>

          <div >
            <img src={profils[this.props.prof].photo}></img>

              <div className = "container">
              <br></br>
                <div class="row">
                  <div className="col-sm-3"></div>
                  <div className="col-sm-3"><h3>{profils[this.props.prof].prenom}</h3></div>
                  <div className="col-sm-3"><h3>{profils[this.props.prof].nom}</h3></div> 
                  <div className="col-sm-3"></div>
                </div> 
              </div>
              <br></br>
              <div><p>{profils[this.props.prof].date}</p></div>
              <br></br>    

          </div>

          </center>

        </div>

      );
    }

  }

}


//class qui permet d'afficher les boutons correspondants aux trois profils proposés
class Choixprofil extends React.Component {
  constructor(props) {
    super(props); 
    this.state = {
      numProf: 0  //numéro profil par défaut
    }
  }


//fonctions qui changent le profil et ses informations en fonction du bouton 
  changeProfil0 = () => {
    numProf=0;
    this.setState({numProf: 0});
  }

  changeProfil1 = () => {
    numProf=1;
    this.setState({numProf: 1});
  }

  changeProfil2 = () => {
    numProf=2;
    this.setState({numProf: 2});
  }


  render() {

    return (

      <div>
        <center>
          <h1 className="fbtitle">Administration</h1>
          <br></br>
        <div className="container">
          <div className="row">
            <div className="col-sm-2"><button onClick={this.changeProfil0}>Widget 1</button></div>
            <div className="col-sm-2"><button onClick={this.changeProfil1}>Widget 2</button></div>
            <div className="col-sm-2"><button onClick={this.changeProfil2}> Widget 3</button></div>
            <div className="col-sm-2"><button onClick={this.changeProfil0}>Widget 4</button></div>
            <div className="col-sm-2"><button onClick={this.changeProfil1}>Widget 5</button></div>
            <div className="col-sm-2"><button onClick={this.changeProfil2}> Widget 6</button></div>
          </div>
        </div>

        <br></br>
        <Profil prof={numProf} />
        </center>  


      </div>

    );
  }
}

export default Choixprofil
