import React, { useEffect, useState } from 'react';
import {
  XYPlot,
  XAxis,
  YAxis,
  HorizontalGridLines,
  VerticalGridLines,
  LineMarkSeries,
  VerticalBarSeries,
  Treemap
} from 'react-vis';
const { default: axios } = require('axios');

// A card used to contain all data from api
export default function Graphs(){

    const [events, setEvents] = useState([]);

    const fetchEvents = async () => {
        await axios.get('http://localhost:9000/albums').then(all => setEvents(all.data));
    }

    useEffect(() => {
        fetchEvents();
        //console.log( {events} );
    }, []);

    var datas = [];
    for (let i = 0; i < events.length; i++) {
        datas.push({x: i, y: events[i].Album.ImageCount});
    }
    console.log("coucou");

    const treeProps = {
        animation: {
          damping: 9,
          stiffness: 300
        },
        data: this.state.treemapData,
        onLeafMouseOver: x => this.setState({hoveredNode: x}),
        onLeafMouseOut: () => this.setState({hoveredNode: false}),
        onLeafClick: () => this.setState({treemapData: _getRandomData()}),
        height: 300,
        mode: this.state.useCirclePacking ? 'circlePack' : 'squarify',
        getLabel: x => x.name,
        width: 350
      };

      function _getRandomData() {
        const totalLeaves = 20 || Math.random() * 20;
        const leaves = [];
        for (let i = 0; i < totalLeaves; i++) {
          leaves.push({
            name: 20 ? 20 : String(Math.random()).slice(0, 3),
            size: Math.random() * 1000,
            color: Math.random(),
            style: {
              border: 'thin solid red'
            }
          });
        }
        return {
          title: '',
          color: 1,
          children: leaves
        };
      }

    return (
        <div className='charts'>
            <div className='chart1'>
                <XYPlot width={500} height={500} margin={70}>
                    <HorizontalGridLines style={{stroke: '#B7E9ED'}} />
                    <VerticalGridLines style={{stroke: '#B7E9ED'}} />
                    <XAxis
                        title="Numéro de l'évènement"
                        style={{
                            line: {stroke: '#ADDDE1'},
                            ticks: {stroke: '#ADDDE1'},
                            text: {stroke: 'none', fill: '#ffffff', fontWeight: 600}
                        }}
                    />
                    <YAxis title="Nombre de photos" style={{text: {stroke: 'none', fill: '#ffffff', fontWeight: 600}}}/>
                    <LineMarkSeries
                        className="first-series"
                        data={datas}
                        style={{
                            strokeWidth: 0.01
                        }}
                    />
                </XYPlot>
            </div>
            <div className='chart2'>
                <Treemap {...treeProps}/>
            </div>
        </div>
    )

}
