import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
const { default: axios } = require('axios');


export default function Map(){
    
    const [pins ,setPins] = useState([]);

    const fecthPins = async () => {
        await axios.get("http://localhost:9000/events").then( all => setPins(all.data));
    }

    useEffect(() => {
        fecthPins();
        console.log( {pins} );
    }, []);

    return(
        <div style={ {width: '100%',height: '100%' } } >
            <LoadScript googleMapsApiKey="AIzaSyBp3yj-pQJb3wB9RfdJ4VTKkjMhvDPxuBg"> 
                <GoogleMap
                    mapContainerStyle={ {height: `400px`} }
                    zoom={12}
                    center={ { lat: 48.851913, lng: 2.286672 } }>
                    
                    <Marker position={ { lat: 48.851913, lng: 2.286672 } } 
                        label={ "ECE" }/>

                    <MarkerMaker/>

                    {pins.map( pin =>{
                        <Marker position={pin.position.coord} 
                            label={pin.date}/>
                    })}

                    
                    
                </GoogleMap>
            </LoadScript>
        </div>
    );
    
}

function MarkerMaker(){

    const pins = [{"event":"Pot des caves","date":"2/12","position":{"name":"Duplex","coord":{"lat":48.873658,"lng":2.2925}},"couvreurs":["Emma"," Tara"]},
                  {"event":"JOB","date":"6/12","position":{"name":"Palais royal","coord":{"lat":48.865045,"lng":2.337404}},"couvreurs":["Eloi"," Thomas"]},
                  {"event":"Afterwork JEECE","date":"8/12","position":{"name":"Square St Eloi","coord":{"lat":48.844737,"lng":2.387503}},"couvreurs":["Nina"," Mathieux"]}];
    
    const pinss = [Pin,Pin];

    return (
        <div>
            {pinss}
        </div>
    );
}

function Pin(){

    const pins = [{"event":"Pot des caves","date":"2/12","position":{"name":"Duplex","coord":{"lat":48.873658,"lng":2.2925}},"couvreurs":["Emma"," Tara"]},
                  {"event":"JOB","date":"6/12","position":{"name":"Palais royal","coord":{"lat":48.865045,"lng":2.337404}},"couvreurs":["Eloi"," Thomas"]},
                  {"event":"Afterwork JEECE","date":"8/12","position":{"name":"Square St Eloi","coord":{"lat":48.844737,"lng":2.387503}},"couvreurs":["Nina"," Mathieux"]}];

    return(
        <>
            <Marker position={pins[0].position.coord} label={pins[0].date}/>
        </>
    );
}

